import{_ as e,c as t,a4 as i,o as r}from"./chunks/framework.07rp2gkU.js";const h=JSON.parse('{"title":"海拔","description":"","frontmatter":{},"headers":[],"relativePath":"material-design/elevation.md","filePath":"material-design/elevation.md","lastUpdated":1755769573000}'),o={name:"material-design/elevation.md"};function n(s,a,l,g,c,p){return r(),t("div",null,a[0]||(a[0]=[i('<h1 id="海拔" tabindex="-1">海拔 <a class="header-anchor" href="#海拔" aria-label="Permalink to &quot;海拔&quot;">​</a></h1><figure><img src="https://lh3.googleusercontent.com/qVW8urfZa3jvqJwERVwpU5e2f5ymWgMZjY3BTLOGR_n2OBwl8n94v0wpsSWgTKNUfGFUZGEoUFzOTNrquB-rJvxit41s5gr7Py0Gq2L33fs38g=s0" alt="" tabindex="0"><figcaption></figcaption></figure><p>不是每个层级都需要阴影，而是仅在需要采用</p><h2 id="改变海拔" tabindex="-1">改变海拔 <a class="header-anchor" href="#改变海拔" aria-label="Permalink to &quot;改变海拔&quot;">​</a></h2><p>组件可以根据用户的交互或系统事件改变海拔（本例中通过添加阴影来实现）</p><figure><img src="https://img.wangj.top/zindex2.esehswvo7.gif" alt="" tabindex="0"><figcaption></figcaption></figure><h2 id="形成海拔" tabindex="-1">形成海拔 <a class="header-anchor" href="#形成海拔" aria-label="Permalink to &quot;形成海拔&quot;">​</a></h2><figure><img src="https://lh3.googleusercontent.com/zRsfQByi4DAtv8IIeKdhr2kIsiYzFTGHUenDsGqz6wz8WYxaiOFEV17pI6YbbA4RYxAlIR8T8SOn0Enm4N9sg1AeBld8_ezOYVseYegHG8ijRw=s0" alt="" tabindex="0"><figcaption></figcaption></figure><p>1 是不同颜色，2 是阴影，3 是蒙层（文章里叫 scrim）</p><h2 id="阴影" tabindex="-1">阴影 <a class="header-anchor" href="#阴影" aria-label="Permalink to &quot;阴影&quot;">​</a></h2><p>阴影的大小、柔和度或扩散程度能表示两个平面之间的距离程度。</p><figure><img src="https://img.wangj.top/image.7ax2207ttj.webp" alt="" tabindex="0"><figcaption></figcaption></figure><p>左图更小、更清晰的阴影表示和背景非常接近；右图更大、更柔和的阴影表示距离很远。</p><h3 id="何时使用阴影" tabindex="-1">何时使用阴影？ <a class="header-anchor" href="#何时使用阴影" aria-label="Permalink to &quot;何时使用阴影？&quot;">​</a></h3><p>当背景有图案或视觉上很复杂时，简单的分隔线无法提供界限，此时就能用阴影。</p><figure><img src="https://lh3.googleusercontent.com/P24wNEodwt-0_nWadPmEJF7OhaLbSj5uEedeU5Duj1IrbRYhs8SFexZ80Ipl_2jE81xyWQY164yYAHa24Z5Q0GOyLrOO7qpqTfr6v6mjVpSF=s0" alt="" tabindex="0"><figcaption></figcaption></figure><h2 id="蒙层" tabindex="-1">蒙层 <a class="header-anchor" href="#蒙层" aria-label="Permalink to &quot;蒙层&quot;">​</a></h2><p>蒙层的颜色使用<code>scrim color role</code>，不透明度为 32%。</p><figure><img src="https://lh3.googleusercontent.com/yRuvEj9pwX6IVcpMFmFKcfUoNAJTYgvhZClKnqXOgW-ALD3bFgtWThUsOY-H7GqEy538_ejZRfXw0NWgpzQKBKg1IqNUXxwzTtiLR_UX23CLKg=s0" alt="" tabindex="0"><figcaption></figcaption></figure>',19)]))}const f=e(o,[["render",n]]);export{h as __pageData,f as default};
